catalog_script_client.script:
function onLoad() 
{
	//get all applications from sys_app table of development instance
	var ga = new GlideAjax('DeploymentAutomationUtil');
	ga.addParam('sysparm_name','populateAllApps');
	ga.getXML(responseCallBack);
}

function responseCallBack(response) {

	//answer will have the app details in the format - appname:appsys_id
	var answer = response.responseXML.documentElement.getAttribute('answer');

	var allAppNamesWithSysID = [];
	allAppNamesWithSysID = answer.split(",");

	for(var i=0; i<allAppNamesWithSysID.length; i++) 
	{
		var appName = allAppNamesWithSysID[i].split(":")[0]; //splitting the app name
		var appSysID = allAppNamesWithSysID[i].split(":")[1]; //splitting the app sys_id
		var version = allAppNamesWithSysID[i].split(":")[2]; //splitting the app sys_id
		var scope = allAppNamesWithSysID[i].split(":")[3]; //splitting the app sys_id
		var value = appSysID+","+version+","+scope;
		g_form.addOption('select_your_application',value,appName);
	}
}
