sys_variable_value.value:
(function execute(inputs, outputs) {

  var response = JSON.parse(inputs.responsebody);
  outputs.statuscode = inputs.statuscode;

  if(inputs.statuscode == 200) 
  {    
    outputs.message = response.message+"\n"+response.sha;
  }
  else {
    outputs.message = inputs.errormessage;
  }

})(inputs, outputs);

