sys_variable_value.value:
(function execute(inputs, outputs) {
  var response = JSON.parse(inputs.ResponseBody);

  if(inputs.StatusCode == "200")
  {
    var GroupNames = [];
    for(var n=0; n<response.result.length; n++)
    {
      var value = response.result[n].name + ":" + response.result[n].sys_id;
      GroupNames.push(value);
    }
    outputs.statuscode = "0";
    outputs.groupnames = GroupNames;
  } 
  else 
  {
    outputs.statuscode = "1";
    outputs.error = inputs.errorMessage;
  }
})(inputs, outputs);

