sys_variable_value.value:
(function execute(inputs, outputs) {

  var response = JSON.parse(inputs.responsebody);
  outputs.statuscode = inputs.statuscode
  if(inputs.statuscode == 201) 
  {  
    outputs.message = "Version "+ response.tag_name +" released successfully with ID #"+response.id;
    outputs.id = response.id;
  }
  else {
    outputs.message = inputs.errormessage;
  }

})(inputs, outputs);

