sys_variable_value.value:
(function execute(inputs, outputs) 
 {
  var rec = new GlideRecord("sys_app");
  rec.addQuery("sys_id",inputs.app_sys_id);
  rec.query();
  if(rec.next()) {
    rec.version = inputs.app_version;
    rec.update();
  }
})(inputs, outputs);

