sys_script_include.script:
var DeploymentAutomationUtil  = Class.create();
DeploymentAutomationUtil .prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
		populateAllApps: function() {
		var appNames = this.getAllApps(this.instance); 
		return appNames;
	},

	getAllApps: function(instance) {
		var inputs = {};
		inputs['instance'] = instance;

		var result = sn_fd.FlowAPI.getRunner()
		.action('global.GetallApplicationfromInstance')
		.inForeground()
		.withInputs(inputs)
		.run();

		var outputs = result.getOutputs();
		var appNames = outputs['appnames'].toString();

		return appNames;
	},


    type: 'DeploymentAutomationUtil '
});
