sys_script_include.script:
var GitHubAutomationUtil = Class.create();
GitHubAutomationUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	checkValidBranch : function() 
	{
		var instance = gs.getProperty('glide.servlet.uri');
		var app_sys_id = this.getParameter("sysparm_app");
		var branch = this.getParameter("sysparm_branch");
		var git_repo = this.getParameter('sysparm_git_repo');
		var project = this.getParameter('sysparm_project');

		//get all the branches from github
		var branches = this.getBranches(project,git_repo);

		if(branches.indexOf(branch) != -1){
			return true;
		}
		else {
			return false;
		}
	},

	getBranches : function(project,repo) {
		gs.info("git_repo in getBranches");
		var inputs = {};

		inputs['username'] = project;
		inputs['repo'] = repo;

		var result = sn_fd.FlowAPI.getRunner()
		.action('global.getallbranchesfromrepo')
		.inForeground()
		.withInputs(inputs)
		.run();

		var outputs = result.getOutputs();
		return (outputs['branches']);
	},

	getRepo : function(app,url) {

		var app_sys_id = this.getParameter("sysparm_app") || app;
		var instance = gs.getProperty('glide.servlet.uri') || url;

		var inputs = {};

		inputs['instance'] = instance;
		inputs['app_sys_id'] = app_sys_id;

		var result = sn_fd.FlowAPI.getRunner()
		.action('global.lookup_git_repo')
		.inForeground()
		.withInputs(inputs)
		.run();

		var outputs = result.getOutputs();
		var obj = {};
		obj.git_repo = outputs['git_repo'];
		obj.user = outputs['user'];
		return new global.JSON().encode(obj);
	},

	type: 'GitHubAutomationUtil'
});
